const group = (cat) => {
  const catTitle = ["癌症治療-癌症基因檢測", "癌症治療-癌症放射治療", "癌症治療-癌症照護"].includes(idList[cat]) ? "" : `<div class="col-12 my-2 px-0 c-title-decoration"><h2 class="d-inline-block m-0 c-pe-3 c-text-title c-bg-white"><strong class="c-text-xx-large">${idList[cat].replace(/-(癌症新療法|癌症基因檢測|癌症放射治療|癌症照護)/, "")}</strong></h2></div>`;
  const subTitle = ["癌症治療-癌症新療法", "癌症治療-癌症基因檢測", "癌症治療-癌症放射治療", "癌症治療-癌症照護"].includes(idList[cat]) ? `<div class="col-12 mt-3 px-0"><h3><strong class="c-text-x-large c-text-title">${idList[cat].replace("癌症治療-", "")}</strong></h3></div>` : "";
  const elements = doctors
    .filter((row) => row.category == cat)
    .map((row) => doctor(row))
    .join("");
  return `
      <div class="row my-4" id="c-${cat}">
          ${catTitle}
          ${subTitle}
          ${elements}
      </div>`;
};
$(() => {
  $("#doctor").html(["bone", "exercise", "brain", "ear", "eye", "cell", "urinary", "aesthetic", "mouth", "stomach", "child", "birth", "wound", "witmed", "precision", "cancer", "cancer_1", "cancer_2", "cancer_3", "cancer_4"].map((e) => group(e)));
});
